        acceleration = 0,
        collision_box = {{-0.5,-0.5},{0.5, 0.5}},
        direction_only = true,
        force_condition = "enemy",
        { layers = {player = true, train = true, [ERMDataHelper.getFlyingLayerName()] =  true} },ollision_mask =  {"player-layer", "train-layer", ERMDataHelper.getFlyingLayerName()},
